<template>
  <a class="button-social"
     :class="buttonSocialDesignObject"
     :page-url="page_url"
     :page-description="page_description"
     :button-design="button_design"
     :title-social="title_social"
     :has-icon="has_icon"
     :has-square-edges="has_square_edges"
     :has-counter="has_counter"
     @click.prevent="showShareWindow"
  >
    <i class="icon-pinterest" v-if="this.$props.has_icon"></i>
    <span class="title-social" v-if="this.$props.title_social">{{ title_social }}</span>
    <span class="counter-pinterest"
          v-model="counter_pinterest"
          v-if="this.$props.has_counter"
    >{{ counter_pinterest }}</span>
  </a>
</template>

<script>
  // Variables
  const description = document.querySelector('meta[name="description"]')
  const image = document.querySelector('link[rel="apple-touch-icon"]')
  
  export default {
    name: 'VueGoodsharePinterest',
    props: {
      page_url: {
        type: String,
        default: document.location.href
      },
      page_description: {
        type: String,
        default: (description) ? description.content : ''
      },
      page_image: {
        type: String,
        default: (image) ? image.src : ''
      },
      button_design: {
        type: String,
        default: 'flat'
      },
      title_social: String,
      has_icon: Boolean,
      has_square_edges: Boolean,
      has_counter: Boolean
    },
    data () {
      return {
        buttonSocialDesignObject: {
          'button-social__square_edges': this.$props.has_square_edges,
          'pinterest__design__flat': this.$props.button_design === 'flat',
          'pinterest__design__gradient': this.$props.button_design === 'gradient',
          'pinterest__design__outline': this.$props.button_design === 'outline'
        },
        counter_pinterest: 0
      }
    },
    methods: {
      /**
       * Get a random integer between `min` and `max`.
       *
       * @param {number} min - min number
       * @param {number} max - max number
       * @return {number} a random integer
       */
      getRandomInt: (min, max) => {
        return Math.floor(Math.random() * (max - min + 1) + min)
      },
      
      /**
       * Slice thousand integer and add `k` letter.
       *
       * @param {number} number - thousand integer
       * @return {string} a integer with letter
       */
      sliceThousandInt: (number) => {
        return (number / 1000).toFixed(1) + 'k'
      },
      
      /**
       * Show share window.
       *
       * @return {object} a pop-up window
       */
      showShareWindow: function () {
        // Variables
        const width = 640
        const height = 640
        let left = (screen.width / 2) - (width / 2)
        let top = (screen.height / 2) - (height / 2)
        const window_config = 'width=' + width + ',height=' + height + ',left=' + left + ',top=' + top + ','
        const share_url = 'https://www.pinterest.com/pin/create/button/?'
          + 'canonicalUrl=' + encodeURIComponent(this.$props.page_url)
          + '&description=' + encodeURIComponent(this.$props.page_description)
          + '&media=' + encodeURIComponent(this.$props.page_image)
        
        return window.open(share_url, 'Share this', window_config + 'toolbar=no,menubar=no,scrollbars=no')
      },
      
      /**
       * Get share counter.
       *
       * @return {object} a share counter
       */
      getShareCounter: function () {
        // Variables
        const script = document.createElement('script')
        const callback = 'vue_goodshare_' + this.getRandomInt(1, 2345)
        
        // Create `script` tag with share count URL
        script.src = 'https://api.pinterest.com/v1/urls/count.json?'
          + 'url=' + encodeURIComponent(this.$props.page_url)
          + '&callback=' + callback
        
        // Add `script` tag with share count URL
        // to end of `body` tag
        document.body.appendChild(script)
        
        // Set share count to `counter_pinterest` v-model
        window[callback] = (count) => {
          if (count) {
            this.counter_pinterest = (count.count >= 1000)
              ? this.sliceThousandInt(count.count)
              : count.count
          }
        }
      }
    },
    mounted () {
      // Show share counter when page loaded
      if (this.$props.has_counter) this.getShareCounter()
    }
  }
</script>

